<template>
    <div class="home">
        <table class="doc-table w100">
            <thead>
                <tr>
                    <td>№ уч.</td>
                    <td>№ каб.</td>
                    <td>  Фото </td>
                    <td>ФИО.</td>
                    <td>Пн</td>
                    <td>Вт</td>
                    <td>Ср</td>
                    <td>Чт</td>
                    <td>Пт</td>
                    <td></td>
                </tr>
            </thead>
            <tbody class="w100">
                <tr v-for="(doctor,index) in doctors" :key="doctor._id" class="w100">
                    <td >{{index + 1}}</td>
                    <td v-if="doctor.spec"> {{getSpecialtyById(doctor.spec)}}</td>
                    <td v-if="doctor.image"> <img class="doc-image" :src="doctor.image"> </td>
                    <td v-if="doctor.name"> {{doctor.name}}</td>
                    <td v-if="doctor.number"> {{doctor.number}}</td>
                    <td v-if="doctor.d1"> {{doctor.d1}}</td>
                    <td v-if="doctor.d2"> {{doctor.d2}}</td>
                    <td v-if="doctor.d3"> {{doctor.d3}}</td>
                    <td v-if="doctor.d4"> {{doctor.d4}}</td>
                    <td v-if="doctor.d5"> {{doctor.d5}}</td>
                </tr>
            </tbody>
        </table>
    </div>
</template>

<script>

    import DoctorService from '../services/DoctorService'
    export default {
        name: 'ViewTable',
        data(){
            return {
                doctors: []
            }
        },
        mounted(){
            return Promise.all([
                DoctorService.getDoctors(),
                DoctorService.getSpecialty()
            ]).then(([doctors,specialty])=>{
                if(doctors){
                    doctors.forEach((doc)=>{
                        doc.image = "data:image/png;base64,";
                    });
                    this.doctors = doctors;
                }
                if(specialty){
                    this.specialty = specialty;
                }
            })
        },
        methods:{
            getSpecialtyById(id){
                let spec = this.specialty.find((special) =>{
                    return special._id == id;
                })
                if (spec) {
                    return spec.name;
                }
                else{
                    return "Доктор, просто доктор"

                }
            }
        }
    }
</script>

<style lang="scss">

    .home{
        margin-top: 20px;
    }
    .w100{
        width: 100%;
    }
    .doc-table{
        border-collapse: collapse;
        .doc-image{
            height: 200px;
        }
    }
    .doc-table td, .doc-table th {
        padding: 10px;
    }
    .doc-table thead{
        background-color: #bfac8f;
    }

    .doc-table tbody th {
        text-align: left;
        font-size: 18px;
    }
    .doc-table tbody tr:nth-child(2n) {
        background: #E5E5E5;
    }

    .doc-table tbody td:last-of-type {
        text-align: center;
    }

    .doc-table tbody tr:hover {
        box-shadow: none;
        top: 2px;
        left: 2px;
        background: #ffd6ab;
    }
</style>
